package bozovic.milos;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Operatori inkrementiranja i dekrementiranja
		
		int i = 10;
		
		i++; // primena operatora inkrementiranja
		System.out.println(i);
		
		/*
		 * i++
		 * i +=
		 * i = i + 1
		 */
		

		// Operator dekrementiranja (umanjenja vrednosti za 1)
		
		float b = 20.56f;
		b--; // -- predstavljaju operator dekrementiranja
	//	b = b - 1;
		System.out.println(b);
			
		
	}

}
